import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { Tablet, Desktop } from "../Responsive";


const Wrapper = styled.div`
  min-height: 50vh;
  margin: 0;
  padding: 0;
  font-family: "Poppins", sans-serif;

  h1 {
    margin: 30px;
    font-family: "Spectral SC", serif;
  }

  h2 {
    font-family: "Lora", serif;
    font-style: normal;
    font-weight: 700;
    /* margin-top: 10px; */
    margin-left: 30px;
    color: var(--primary-color);
  }
  .featured {
    padding: 20px;
    margin-bottom: 0;
  }
  .featured .img-container {
    width: 100%;
    height: 40vh;
    background-color: grey;
    margin: auto;
    /* border-radius: 20px; */
    ${Tablet({
    width: "80%",
    height: "60vh",
    // borderRadius: "30px",
})}/* ${Desktop({
    width: "70%",
})} */
  }
  .featured img {
    height: 100%;
    width: 100%;
    object-fit: cover;
    /* border-radius: 20px;
    ${Tablet({ borderRadius: "30px" })} */
  }
  .featured h3 {
    font-family: Georgia, "Times New Roman", Times, serif;
    font-size: 30px;
    margin: 20px 0;
    font-weight: 700;
    text-align: center;
  }
  .featured p {
    text-align: center;
  }
  .featured .post {
    /* margin-bottom: 30px; */
    padding-bottom: 20px;
    /* ${Tablet({ paddingBottom: "50px" })} */
  }
  .post a:hover {
    opacity: 0.6;
  }
  .others {
    padding: 20px;
    display: grid;
    grid-template-columns: 100%;
    ${Tablet({ gridTemplateColumns: "45% 45%", paddingLeft: '40px' })};
   
  }
  .others .post {
    margin-bottom: 30px;
    box-shadow: 0 5px 5px #888888;
    padding: 20px;
    ${Tablet({ marginRight: "40px" })}
    ${Desktop({ marginRight: "50px" })}
  }
  .others h4 {
    font-family: "Noto Serif";
    font-style: italic;
    font-weight: 700;
  }
  .others span {
    color: var(--primary-color);
    font-weight: 500;
  }
  .more {
    margin: 30px;
    margin-top: 20px;
  }
  .more a {
    padding: 10px 15px;
    background: var(--primary-color);
    color: white;
    font-size: 20px;
    font-weight: 500;
    border-radius: 30px;
    text-decoration: none;
  }
  .more a:hover {
    color: #0a0f83;
    font-weight: 700;
  }
`;

const Loading1 = () => {
    return (
        <div className="featured">
            <div className="px-3">
                <Skeleton height={350} />
            </div>
        </div>
    );
};

const Loading2 = () => {
    return (
        <div className="others">
            <div className="me-md-2">
                <Skeleton height={150} />
            </div>
            <div className="">
                <Skeleton height={150} />
            </div>
        </div>
    );
};


const Home = () => {


    return (
        <Wrapper>
            <h1>
                Welcome to ExpertGuide Blog
            </h1>

            {loading ? <Loading1 /> :
                featuredPost.length !== 0 && (
                    <>
                        <h2 className="">Trending news</h2>
                        <div className="featured">{featuredItems}</div>
                    </>
                )
            }
            {loading ? <Loading2 /> :
                posts.length !== 0 && (
                    <>
                        <h2>Latest news</h2>
                        <div className="others">{otherItems}</div>
                        <div className="more">
                            <Link to="/posts">More News</Link>
                        </div>
                    </>
                )
            }
        </Wrapper>
    );
};

export default Home;
